require(["gitbook"], function(gitbook) {
    gitbook.events.bind("page.change", function() {
      $('.summary').addClass('nano-content');
      $('nav').nanoScroller({alwaysVisible: false});

      $('.body-inner').addClass('nano-content');
      $('.book-body').nanoScroller({alwaysVisible: false});

      /// Change index entry in summary to onedata logo
      $('[data-path="index.html"] a').html('<h1><img src="/doc/img/heading.png" /> documentation</h1>');

      /// Disable glossary in headings
      for (var i=1; i<=5; ++i) {
        var headers = $('.page-inner h'+i);
        if (headers) {
          headers.each(function() {
            var a = $(this).find('a');
            var content = a.html();
            a.remove();
            $(this).prepend(content);
          });
        }


      }
    });

    gitbook.events.bind("exercise.submit", function() {
        // do something
    });
});
